"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Badge } from "@/components/ui/badge"
import { QrCode, RefreshCw, Clock, User, Loader2, CheckCircle, AlertCircle } from "lucide-react"
import { useRouter } from "next/navigation"
import Image from "next/image"

interface VotingSession {
  id: string
  qrCode: string
  redeemCode: string
  qrCodeImage: string
  isValidated: boolean
  isUsed: boolean
  expiresAt: string
}

interface User {
  id: string
  name: string
  email: string
  nim: string
  role: string
  hasVoted: boolean
}

export default function GenerateCodePage() {
  const [user, setUser] = useState<User | null>(null)
  const [votingSession, setVotingSession] = useState<VotingSession | null>(null)
  const [loading, setLoading] = useState(true)
  const [generating, setGenerating] = useState(false)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [timeRemaining, setTimeRemaining] = useState("")
  const router = useRouter()

  useEffect(() => {
    const checkAuthAndData = async () => {
      try {
        console.log('Checking auth and data...')

        // Get user session
        const response = await fetch('/api/auth/me', {
          method: 'GET',
          credentials: 'include', // Important untuk cookies
          headers: {
            'Content-Type': 'application/json',
          },
        })

        console.log('Auth response status:', response.status)

        if (!response.ok) {
          console.log('Not authenticated, redirecting to login')
          router.push("/login")
          return
        }

        const userData = await response.json()
        console.log('User data:', userData)

        if (userData.hasVoted) {
          console.log('User already voted, redirecting to success')
          router.push("/success")
          return
        }

        setUser(userData)

        // Check for existing voting session
        console.log('Checking for existing voting session...')
        const sessionResponse = await fetch('/api/qr-code', {
          method: 'GET',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
          },
        })

        console.log('Session response status:', sessionResponse.status)

        if (sessionResponse.ok) {
          const sessionData = await sessionResponse.json()
          console.log('Session data:', sessionData)
          setVotingSession(sessionData.session)

          if (sessionData.session?.isValidated) {
            console.log('Session already validated, redirecting to vote')
            router.push("/vote")
            return
          }
        } else if (sessionResponse.status !== 404) {
          // 404 is expected if no session exists, other errors should be logged
          const errorData = await sessionResponse.json().catch(() => ({}))
          console.error('Session check error:', errorData)
        }
      } catch (err) {
        console.error('Auth check error:', err)
        setError("Terjadi kesalahan saat memuat data: " + (err instanceof Error ? err.message : 'Unknown error'))
      } finally {
        setLoading(false)
      }
    }

    checkAuthAndData()
  }, [router])

  // Update time remaining every second
  useEffect(() => {
    if (!votingSession) return

    const updateTime = () => {
      const now = new Date()
      const expiresAt = new Date(votingSession.expiresAt)
      const diff = expiresAt.getTime() - now.getTime()

      if (diff <= 0) {
        setTimeRemaining("Expired")
        setVotingSession(null)
        return
      }

      const hours = Math.floor(diff / (1000 * 60 * 60))
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60))
      const seconds = Math.floor((diff % (1000 * 60)) / 1000)

      if (hours > 0) {
        setTimeRemaining(`${hours}:${minutes.toString().padStart(2, "0")}:${seconds.toString().padStart(2, "0")}`)
      } else {
        setTimeRemaining(`${minutes}:${seconds.toString().padStart(2, "0")}`)
      }
    }

    updateTime()
    const interval = setInterval(updateTime, 1000)

    return () => clearInterval(interval)
  }, [votingSession])

  // Check for validation status periodically
  useEffect(() => {
    if (!votingSession || votingSession.isValidated) return

    const checkValidation = async () => {
      try {
        console.log('Checking validation status...')
        const response = await fetch('/api/qr-code', {
          method: 'GET',
          credentials: 'include',
        })

        if (response.ok) {
          const data = await response.json()
          console.log('Validation check response:', data)

          if (data.session?.isValidated && !votingSession.isValidated) {
            console.log('Session validated, updating state and redirecting')
            setVotingSession(data.session)
            setTimeout(() => {
              router.push("/vote")
            }, 1000) // Small delay to show success message
          }
        }
      } catch (err) {
        console.error('Validation check error:', err)
      }
    }

    const interval = setInterval(checkValidation, 3000) // Check every 3 seconds
    return () => clearInterval(interval)
  }, [votingSession, router])

  const generateQRCode = async () => {
    if (!user) {
      setError("User data tidak tersedia")
      return
    }

    setGenerating(true)
    setError("")
    setSuccess("")

    try {
      console.log('Generating QR code for user:', user.id)

      const response = await fetch('/api/qr-code', {
        method: 'POST',
        credentials: 'include', // Important untuk cookies
        headers: {
          'Content-Type': 'application/json',
        },
      })

      console.log('Generate QR response status:', response.status)

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}))
        console.error('Generate QR error response:', errorData)
        throw new Error(errorData.error || `Server error: ${response.status}`)
      }

      const data = await response.json()
      console.log('Generated QR data:', data)

      if (data.session) {
        setVotingSession(data.session)
        setSuccess("QR Code berhasil dibuat!")
      } else {
        throw new Error('Invalid response format')
      }
    } catch (err) {
      console.error('Generate QR error:', err)
      const errorMessage = err instanceof Error ? err.message : "Terjadi kesalahan saat membuat kode QR"
      setError(errorMessage)
    } finally {
      setGenerating(false)
    }
  }

  const refreshCode = async () => {
    if (!votingSession) return

    setVotingSession(null)
    await generateQRCode()
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data...</p>
        </div>

        {/* Alert Messages */}
        <div className="mb-6 space-y-4">
          {success && (
            <Alert className="bg-green-50 border-green-200">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-800">{success}</AlertDescription>
            </Alert>
          )}

          {error && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-2xl py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <QrCode className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Generate Kode Voting</p>
            </div>
          </div>
        </div>

            {success && (
              <Alert className="bg-green-50 border-green-200">
                <CheckCircle className="h-4 w-4 text-green-600" />
                <AlertDescription className="text-green-800">{success}</AlertDescription>
              </Alert>
            )}

            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

        {/* User Info */}
        {user && (
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Informasi Pemilih
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Nama</p>
                  <p className="font-semibold">{user.name}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Email</p>
                  <p className="font-semibold">{user.email}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">NIM</p>
                  <p className="font-semibold">{user.nim}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Status</p>
                  <Badge variant={user.hasVoted ? "default" : "secondary"}>
                    {user.hasVoted ? "Sudah Voting" : "Belum Voting"}
                  </Badge>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* QR Code Section */}
        {!votingSession ? (
          <Card>
            <CardHeader className="text-center">
              <CardTitle>Generate Kode Voting</CardTitle>
              <CardDescription>
                Klik tombol di bawah untuk membuat kode QR dan kode redeem yang akan digunakan untuk validasi voting
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center space-y-4">
              <Button onClick={generateQRCode} disabled={generating} size="lg">
                {generating ? (
                  <>
                    <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                    Membuat Kode...
                  </>
                ) : (
                  <>
                    <QrCode className="mr-2 h-5 w-5" />
                    Generate Kode QR
                  </>
                )}
              </Button>

              {/* Debug Info */}
              {process.env.NODE_ENV === 'development' && (
                <div className="mt-4 p-4 bg-gray-100 rounded-lg text-left">
                  <h4 className="font-bold text-sm mb-2">Debug Info:</h4>
                  <div className="text-xs space-y-1">
                    <p>User ID: {user?.id || 'Not loaded'}</p>
                    <p>User Email: {user?.email || 'Not loaded'}</p>
                    <p>Has Voted: {user?.hasVoted ? 'Yes' : 'No'}</p>
                    <p>Loading: {loading ? 'Yes' : 'No'}</p>
                    <p>Generating: {generating ? 'Yes' : 'No'}</p>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {/* QR Code Display */}
            <Card>
              <CardHeader className="text-center">
                <CardTitle className="flex items-center justify-center gap-2">
                  <QrCode className="h-5 w-5" />
                  Kode QR Voting
                </CardTitle>
                <CardDescription>Tunjukkan kode QR ini kepada panitia untuk validasi</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center space-y-4">
                  <div className="flex justify-center">
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <Image
                        src={votingSession.qrCodeImage}
                        alt="QR Code"
                        width={200}
                        height={200}
                        className="mx-auto"
                      />
                    </div>
                  </div>

                  <div className="flex items-center justify-center gap-2">
                    <Clock className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm text-muted-foreground">
                      Berlaku hingga: {timeRemaining}
                    </span>
                  </div>

                  {votingSession.isValidated ? (
                    <Alert>
                      <CheckCircle className="h-4 w-4" />
                      <AlertDescription>
                        Kode Anda sudah divalidasi! Anda akan diarahkan ke halaman voting.
                      </AlertDescription>
                    </Alert>
                  ) : (
                    <Button onClick={refreshCode} variant="outline" size="sm">
                      <RefreshCw className="mr-2 h-4 w-4" />
                      Generate Ulang
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Redeem Code */}
            <Card>
              <CardHeader className="text-center">
                <CardTitle>Kode Redeem</CardTitle>
                <CardDescription>Alternatif jika QR code tidak dapat dipindai</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center">
                  <div className="inline-flex items-center justify-center p-4 bg-muted rounded-lg">
                    <span className="text-2xl font-mono font-bold tracking-wider">
                      {votingSession.redeemCode}
                    </span>
                  </div>
                  <p className="text-sm text-muted-foreground mt-2">
                    Berikan kode ini kepada panitia jika QR code tidak dapat dipindai
                  </p>
                </div>
              </CardContent>
            </Card>

            {/* Instructions */}
            <Card className="bg-muted/50">
              <CardHeader>
                <CardTitle className="text-lg">Langkah Selanjutnya</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 text-sm">
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      1
                    </div>
                    <p>Tunjukkan kode QR atau berikan kode redeem kepada panitia</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      2
                    </div>
                    <p>Tunggu panitia memvalidasi identitas dan kode Anda</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      3
                    </div>
                    <p>Setelah divalidasi, Anda akan diarahkan ke halaman voting</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  )
}

app/login/page.tsx.content:
"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Vote, ArrowLeft, Loader2 } from "@/lib/icons"
import Link from "next/link"
import { useRouter, useSearchParams } from "next/navigation"

export default function LoginPage() {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const router = useRouter()
  const searchParams = useSearchParams()
  const redirect = searchParams.get("redirect")

  // Cek apakah user sudah login â†’ redirect langsung
  useEffect(() => {
    const checkSession = async () => {
      try {
        const res = await fetch("/api/auth/me", {
          credentials: "include"
        })
        if (res.ok) {
          const { user } = await res.json()
          if (redirect) {
            router.replace(redirect)
            return
          }
          if (user.role === "SUPER_ADMIN") {
            router.replace("/super-admin")
          } else if (user.role === "ADMIN") {
            router.replace("/admin")
          } else {
            router.replace(user.hasVoted ? "/success" : "/generate-code")
          }
        }
      } catch (err) {
        console.error("Session check error:", err)
      }
    }
    checkSession()
  }, [redirect, router])

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ email, password }),
      })

      const result = await response.json()

      if (!response.ok) {
        setError(result.error || "Email atau password salah")
        return
      }

      console.log("Login successful:", result.user)

      // Tunggu cookie terset
      await new Promise((resolve) => setTimeout(resolve, 100))

      // Redirect prioritas: query ?redirect
      if (redirect) {
        router.replace(redirect)
        return
      }

      // Redirect default berdasarkan role
      const user = result.user
      if (user.role === "SUPER_ADMIN") {
        router.replace("/super-admin")
      } else if (user.role === "ADMIN") {
        router.replace("/admin")
      } else {
        router.replace(user.hasVoted ? "/success" : "/generate-code")
      }
    } catch (err) {
      console.error("Login error:", err)
      setError("Terjadi kesalahan saat login")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <Link
            href="/"
            className="inline-flex items-center gap-2 text-muted-foreground hover:text-foreground mb-6"
          >
            <ArrowLeft className="h-4 w-4" />
            Kembali ke Beranda
          </Link>
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Vote className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Pemilihan Presiden Mahasiswa</p>
            </div>
          </div>
        </div>

        {/* Login Form */}
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl">Login</CardTitle>
            <CardDescription>Masukkan email dan password Anda</CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="space-y-4">
              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="email@example.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <Input
                  id="password"
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              <Button type="submit" className="w-full" disabled={loading}>
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Memproses...
                  </>
                ) : (
                  <>
                    Masuk
                    <Vote className="ml-2 h-4 w-4" />
                  </>
                )}
              </Button>
            </form>

            <div className="mt-4 text-center">
              <Link
                href="/forgot-password"
                className="text-sm text-blue-600 hover:underline"
              >
                Lupa Password?
              </Link>
            </div>

            <div className="mt-6 text-center">
              <p className="text-sm text-muted-foreground">
                Belum punya akun?{" "}
                <Button variant="link" className="p-0 h-auto font-normal">
                  Hubungi admin untuk registrasi
                </Button>
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

app/register/page.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Vote, User, Loader2, CheckCircle } from "lucide-react"
import { useRouter } from "next/navigation"
import Link from "next/link"

const PRODI_OPTIONS = [
  "Teknik Informatika",
  "Teknik Sipil",
  "Teknik Elektro",
  "Teknik Kimia",
  "Teknik Mesin",
  "Teknik Industri",
  "Teknik Geologi",
  "Teknik Geofisika",
  "Teknik Lingkungan",
  "Arsitektur",
  "Perencanaan Wilayah dan Kota",
  "Teknik Biosistem",
  "Sains Aktuaria",
  "Matematika",
  "Fisika",
  "Kimia",
  "Biologi",
  "Farmasi",
]

export default function RegisterPage() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    nim: "",
    name: "",
    prodi: "",
    gender: "",
    phone: "",
  })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const router = useRouter()

  const handleInputChange = (field: string, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }))
  }

  const validateNIM = (nim: string) => {
    // Basic NIM validation for ITERA (usually 9 digits starting with specific patterns)
    const nimPattern = /^[0-9]{9}$/
    return nimPattern.test(nim)
  }

  const validatePhone = (phone: string) => {
    // Indonesian phone number validation
    const phonePattern = /^(\+62|62|0)[0-9]{9,12}$/
    return phonePattern.test(phone)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    // Validation
    if (!validateNIM(formData.nim)) {
      setError("NIM harus berupa 9 digit angka")
      setLoading(false)
      return
    }

    if (formData.phone && !validatePhone(formData.phone)) {
      setError("Format nomor telepon tidak valid")
      setLoading(false)
      return
    }

    try {
      // Call register API
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
          nim: formData.nim,
          name: formData.name,
          prodi: formData.prodi,
          gender: formData.gender,
          phone: formData.phone || null,
        }),
      })

      const result = await response.json()

      if (!response.ok) {
        setError(result.error || "Terjadi kesalahan saat registrasi")
        return
      }

      // Registration successful, redirect to login
      router.push("/login")
    } catch (err) {
      setError("Terjadi kesalahan saat registrasi")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        {/* Header */}
        <div className="text-center mb-8">
          <Link href="/" className="inline-flex items-center gap-2 text-muted-foreground hover:text-foreground mb-6">
            â† Kembali ke Beranda
          </Link>
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Vote className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Registrasi Pemilih</p>
            </div>
          </div>
        </div>

        {/* Registration Form */}
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl flex items-center justify-center gap-2">
              <User className="h-6 w-6" />
              Daftar Akun Baru
            </CardTitle>
            <CardDescription>
              Isi data lengkap untuk membuat akun dan berpartisipasi dalam pemilihan
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              {/* Email */}
              <div className="space-y-2">
                <Label htmlFor="email">Email *</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="email@example.com"
                  value={formData.email}
                  onChange={(e) => handleInputChange("email", e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              {/* Password */}
              <div className="space-y-2">
                <Label htmlFor="password">Password *</Label>
                <Input
                  id="password"
                  type="password"
                  placeholder="Password"
                  value={formData.password}
                  onChange={(e) => handleInputChange("password", e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              {/* NIM */}
              <div className="space-y-2">
                <Label htmlFor="nim">NIM (Nomor Induk Mahasiswa) *</Label>
                <Input
                  id="nim"
                  type="text"
                  placeholder="Contoh: 121450001"
                  value={formData.nim}
                  onChange={(e) => handleInputChange("nim", e.target.value)}
                  required
                  disabled={loading}
                  maxLength={9}
                />
                <p className="text-xs text-muted-foreground">Masukkan 9 digit NIM Anda</p>
              </div>

              {/* Name */}
              <div className="space-y-2">
                <Label htmlFor="name">Nama Lengkap *</Label>
                <Input
                  id="name"
                  type="text"
                  placeholder="Masukkan nama lengkap sesuai KTM"
                  value={formData.name}
                  onChange={(e) => handleInputChange("name", e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              {/* Program Studi */}
              <div className="space-y-2">
                <Label htmlFor="prodi">Program Studi *</Label>
                <Select
                  value={formData.prodi}
                  onValueChange={(value) => handleInputChange("prodi", value)}
                  disabled={loading}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Pilih program studi Anda" />
                  </SelectTrigger>
                  <SelectContent>
                    {PRODI_OPTIONS.map((prodi) => (
                      <SelectItem key={prodi} value={prodi}>
                        {prodi}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Gender */}
              <div className="space-y-2">
                <Label htmlFor="gender">Jenis Kelamin *</Label>
                <Select
                  value={formData.gender}
                  onValueChange={(value) => handleInputChange("gender", value)}
                  disabled={loading}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Pilih jenis kelamin" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="L">Laki-laki</SelectItem>
                    <SelectItem value="P">Perempuan</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Phone */}
              <div className="space-y-2">
                <Label htmlFor="phone">Nomor Telepon</Label>
                <Input
                  id="phone"
                  type="tel"
                  placeholder="Contoh: 081234567890"
                  value={formData.phone}
                  onChange={(e) => handleInputChange("phone", e.target.value)}
                  disabled={loading}
                />
                <p className="text-xs text-muted-foreground">Opsional - untuk keperluan komunikasi jika diperlukan</p>
              </div>

              <Button type="submit" className="w-full" disabled={loading}>
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Mendaftar...
                  </>
                ) : (
                  <>
                    <CheckCircle className="mr-2 h-4 w-4" />
                    Daftar Akun
                  </>
                )}
              </Button>
            </form>

            <div className="mt-6 text-center">
              <p className="text-sm text-muted-foreground">
                Sudah punya akun?{" "}
                <Link href="/login" className="text-primary hover:underline">
                  Login di sini
                </Link>
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Info Card */}
        <Card className="mt-6 bg-muted/50">
          <CardContent className="pt-6">
            <div className="text-center">
              <h3 className="font-semibold mb-2">Informasi Penting</h3>
              <div className="text-sm text-muted-foreground space-y-2">
                <p>â€¢ Pastikan data yang Anda masukkan sesuai dengan KTM</p>
                <p>â€¢ NIM yang sudah terdaftar tidak dapat digunakan lagi</p>
                <p>â€¢ Setelah registrasi berhasil, silakan login untuk melanjutkan</p>
                <p>â€¢ Hubungi panitia jika mengalami kesulitan: pemilu@itera.ac.id</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

app/success/page.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { CheckCircle, Vote, User, Calendar, LogOut, Loader2 } from "lucide-react"
import { useRouter } from "next/navigation"
import Image from "next/image"

interface User {
  id: string
  name: string
  email: string
  nim: string
  hasVoted: boolean
}

interface VoteData {
  id: string
  candidateName: string
  candidateNim: string
  createdAt: string
}

interface VotingStats {
  candidateId: string
  candidateName: string
  voteCount: number
  percentage: string
}

export default function SuccessPage() {
  const [user, setUser] = useState<User | null>(null)
  const [voteData, setVoteData] = useState<VoteData | null>(null)
  const [votingStats, setVotingStats] = useState<VotingStats[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const router = useRouter()

  useEffect(() => {
    const loadData = async () => {
      try {
        // Check authentication
        const authResponse = await fetch('/api/auth/me', {
          method: 'GET',
          credentials: 'include',
        })

        if (!authResponse.ok) {
          router.push("/login")
          return
        }

        const userData = await authResponse.json()

        if (!userData.hasVoted) {
          router.push("/generate-code")
          return
        }

        setUser(userData)

        // Get voting status including vote data
        const statusResponse = await fetch('/api/voting-status', {
          method: 'GET',
          credentials: 'include',
        })

        if (statusResponse.ok) {
          const statusData = await statusResponse.json()
          if (statusData.vote) {
            setVoteData(statusData.vote)
          }
        }

        // Get voting statistics
        const statsResponse = await fetch('/api/vote', {
          method: 'GET',
          credentials: 'include',
        })

        if (statsResponse.ok) {
          const statsData = await statsResponse.json()
          setVotingStats(statsData.stats || [])
        }

      } catch (err) {
        console.error("Error loading data:", err)
        setError("Terjadi kesalahan saat memuat data")
      } finally {
        setLoading(false)
      }
    }

    loadData()
  }, [router])

  const handleLogout = async () => {
    try {
      // Call logout API if you have one, or just clear cookies
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include',
      })

      // Redirect regardless of logout API response
      router.push("/")
    } catch (err) {
      // Still redirect even if logout fails
      router.push("/")
    }
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data...</p>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600">{error}</p>
          <Button onClick={() => router.push("/")} className="mt-4">
            Kembali ke Beranda
          </Button>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-2xl py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-green-500 flex items-center justify-center">
              <CheckCircle className="h-7 w-7 text-white" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Vote Berhasil</p>
            </div>
          </div>
        </div>

        <div className="space-y-6">
          {/* Success Message */}
          <Card className="bg-green-50 border-green-200">
            <CardHeader className="text-center">
              <div className="mx-auto w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mb-4">
                <CheckCircle className="h-8 w-8 text-white" />
              </div>
              <CardTitle className="text-2xl text-green-800">Vote Berhasil Disimpan!</CardTitle>
              <CardDescription className="text-green-700">
                Terima kasih telah berpartisipasi dalam Pemilihan Presiden Mahasiswa ITERA 2024
              </CardDescription>
            </CardHeader>
          </Card>

          {/* Vote Summary */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Vote className="h-5 w-5" />
                Ringkasan Vote
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <p className="text-sm text-muted-foreground">Pemilih</p>
                    <p className="font-semibold">{user?.name}</p>
                    <p className="text-sm text-muted-foreground">{user?.email}</p>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground">Waktu Vote</p>
                    <p className="font-semibold">
                      {voteData?.createdAt
                        ? new Date(voteData.createdAt).toLocaleString("id-ID", {
                            dateStyle: "full",
                            timeStyle: "short",
                          })
                        : new Date().toLocaleString("id-ID", {
                            dateStyle: "full",
                            timeStyle: "short",
                          })}
                    </p>
                  </div>
                </div>

                {voteData && (
                  <div className="border-t pt-4">
                    <p className="text-sm text-muted-foreground mb-2">Kandidat yang Dipilih</p>
                    <div className="flex items-center gap-4 p-4 bg-muted/50 rounded-lg">
                      <div className="relative w-16 h-16">
                        <div className="w-full h-full rounded-full bg-gray-200 flex items-center justify-center">
                          <User className="h-8 w-8 text-gray-400" />
                        </div>
                      </div>
                      <div>
                        <h3 className="font-semibold text-lg">{voteData.candidateName}</h3>
                        <p className="text-sm text-muted-foreground">
                          NIM: {voteData.candidateNim}
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Status */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Status Pemilih
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center justify-between">
                <div>
                  <p className="font-semibold">Status Vote</p>
                  <p className="text-sm text-muted-foreground">Anda telah berhasil memberikan suara</p>
                </div>
                <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                  <CheckCircle className="mr-1 h-3 w-3" />
                  Sudah Vote
                </Badge>
              </div>
            </CardContent>
          </Card>

          {/* Voting Results Preview */}
          {votingStats.length > 0 && (
            <Card className="bg-blue-50 border-blue-200">
              <CardHeader>
                <CardTitle className="text-blue-800">Hasil Sementara</CardTitle>
                <CardDescription className="text-blue-600">
                  Data real-time dari sistem voting
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {votingStats
                    .sort((a, b) => b.voteCount - a.voteCount)
                    .map((candidate, index) => (
                      <div key={candidate.candidateId} className="flex items-center justify-between p-3 bg-white rounded-lg">
                        <div className="flex items-center gap-3">
                          <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center text-sm font-bold text-blue-800">
                            {index + 1}
                          </div>
                          <div>
                            <p className="font-semibold text-sm">{candidate.candidateName}</p>
                            <p className="text-xs text-muted-foreground">{candidate.percentage}%</p>
                          </div>
                        </div>
                        <div className="text-right">
                          <p className="font-bold text-blue-800">{candidate.voteCount}</p>
                          <p className="text-xs text-muted-foreground">suara</p>
                        </div>
                      </div>
                    ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Important Notes */}
          <Card className="bg-yellow-50 border-yellow-200">
            <CardHeader>
              <CardTitle className="text-yellow-800">Informasi Penting</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2 text-sm text-yellow-700">
                <p>â€¢ Vote Anda telah tersimpan dengan aman dalam sistem</p>
                <p>â€¢ Identitas pemilih dijaga kerahasiaan sesuai prinsip pemilu</p>
                <p>â€¢ Hasil pemilihan akan diumumkan setelah periode voting berakhir</p>
                <p>â€¢ Terima kasih atas partisipasi Anda dalam demokrasi kampus</p>
              </div>
            </CardContent>
          </Card>

          {/* Actions */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Button variant="outline" onClick={() => router.push("/")} className="flex items-center gap-2">
              <Calendar className="h-4 w-4" />
              Kembali ke Beranda
            </Button>
            <Button onClick={handleLogout} variant="secondary" className="flex items-center gap-2">
              <LogOut className="h-4 w-4" />
              Logout
            </Button>
          </div>

          {/* Footer */}
          <div className="text-center text-sm text-muted-foreground pt-8 border-t">
            <p>Pemilihan Presiden Mahasiswa ITERA 2024</p>
            <p>Sistem Voting Digital - Institut Teknologi Sumatera</p>
          </div>
        </div>
      </div>
    </div>
  )
}

app/vote/page.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Vote, User, CheckCircle, Loader2, AlertCircle, Eye } from "lucide-react"
import { useRouter } from "next/navigation"
import Image from "next/image"

interface Candidate {
  id: string
  name: string
  nim: string
  prodi: string
  visi: string
  misi: string
  photo?: string
  isActive: boolean
}

interface User {
  id: string
  name: string
  email: string
  nim: string
  role: string
  hasVoted: boolean
}

export default function VotePage() {
  const [user, setUser] = useState<User | null>(null)
  const [candidates, setCandidates] = useState<Candidate[]>([])
  const [selectedCandidate, setSelectedCandidate] = useState<string>("")
  const [loading, setLoading] = useState(true)
  const [voting, setVoting] = useState(false)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [showConfirmDialog, setShowConfirmDialog] = useState(false)
  const [showDetailDialog, setShowDetailDialog] = useState(false)
  const [detailCandidate, setDetailCandidate] = useState<Candidate | null>(null)
  const router = useRouter()

  useEffect(() => {
    const checkAuthAndLoadData = async () => {
      try {
        console.log('Checking auth and loading data...')

        // Check authentication
        const authResponse = await fetch('/api/auth/me', {
          method: 'GET',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
          },
        })

        if (!authResponse.ok) {
          console.log('Not authenticated, redirecting to login')
          router.push("/login")
          return
        }

        const userData = await authResponse.json()
        console.log('User data:', userData)

        if (userData.hasVoted) {
          console.log('User already voted, redirecting to success')
          router.push("/success")
          return
        }

        setUser(userData)

        // Check if user has validated voting session
        const sessionResponse = await fetch('/api/qr-code', {
          method: 'GET',
          credentials: 'include',
        })

        if (!sessionResponse.ok) {
          console.log('No voting session, redirecting to generate-code')
          router.push("/generate-code")
          return
        }

        const sessionData = await sessionResponse.json()
        console.log('Session data:', sessionData)

        if (!sessionData.session?.isValidated) {
          console.log('Session not validated, redirecting to generate-code')
          router.push("/generate-code")
          return
        }

        if (sessionData.session?.isUsed) {
          console.log('Session already used, redirecting to success')
          router.push("/success")
          return
        }

        // Load candidates
        console.log('Loading candidates...')
        const candidatesResponse = await fetch('/api/candidates', {
          method: 'GET',
          credentials: 'include',
        })

        if (!candidatesResponse.ok) {
          throw new Error('Failed to load candidates')
        }

        const candidatesData = await candidatesResponse.json()
        console.log('Candidates data:', candidatesData)

        setCandidates(candidatesData.candidates || [])

        if (candidatesData.candidates?.length === 0) {
          setError('Tidak ada kandidat yang tersedia saat ini')
        }

      } catch (err) {
        console.error('Error loading data:', err)
        setError("Terjadi kesalahan saat memuat data: " + (err instanceof Error ? err.message : 'Unknown error'))
      } finally {
        setLoading(false)
      }
    }

    checkAuthAndLoadData()
  }, [router])

  const handleVoteSubmit = async () => {
    if (!selectedCandidate || !user) return

    setVoting(true)
    setError("")
    setSuccess("")

    try {
      console.log('Submitting vote for candidate:', selectedCandidate)

      const response = await fetch('/api/vote', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          candidateId: selectedCandidate
        })
      })

      const data = await response.json()
      console.log('Vote response:', data)

      if (!response.ok) {
        throw new Error(data.error || 'Failed to submit vote')
      }

      setSuccess('Vote berhasil disimpan!')

      // Redirect to success page after short delay
      setTimeout(() => {
        router.push("/success")
      }, 2000)

    } catch (err) {
      console.error('Vote error:', err)
      setError(err instanceof Error ? err.message : "Terjadi kesalahan saat menyimpan vote")
      setShowConfirmDialog(false)
    } finally {
      setVoting(false)
    }
  }

  const handleCandidateSelect = (candidateId: string) => {
    setSelectedCandidate(candidateId)
  }

  const handleShowDetail = (candidate: Candidate) => {
    setDetailCandidate(candidate)
    setShowDetailDialog(true)
  }

  const handleConfirmVote = () => {
    if (!selectedCandidate) return
    setShowConfirmDialog(true)
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data kandidat...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-4xl py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Vote className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Pilih Kandidat</p>
            </div>
          </div>
        </div>

        {/* Alert Messages */}
        <div className="mb-6 space-y-4">
          {success && (
            <Alert className="bg-green-50 border-green-200">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-800">{success}</AlertDescription>
            </Alert>
          )}

          {error && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
        </div>

        {/* Voter Info */}
        {user && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Informasi Pemilih
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Nama</p>
                  <p className="font-semibold">{user.name}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Email</p>
                  <p className="font-semibold">{user.email}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">NIM</p>
                  <p className="font-semibold">{user.nim}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Instructions */}
        <Alert className="mb-8">
          <CheckCircle className="h-4 w-4" />
          <AlertDescription>
            <strong>Petunjuk Voting:</strong> Pilih salah satu kandidat di bawah ini dengan mengklik kartu kandidat.
            Setelah memilih, klik tombol "Vote" untuk mengonfirmasi pilihan Anda. Pilihan tidak dapat diubah setelah
            dikonfirmasi.
          </AlertDescription>
        </Alert>

        {/* Candidates Grid */}
        {candidates.length > 0 ? (
          <>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {candidates.map((candidate) => (
                <Card
                  key={candidate.id}
                  className={`cursor-pointer transition-all duration-200 hover:shadow-lg ${
                    selectedCandidate === candidate.id ? "ring-2 ring-primary bg-primary/5" : "hover:shadow-md"
                  }`}
                  onClick={() => handleCandidateSelect(candidate.id)}
                >
                  <CardHeader className="text-center">
                    <div className="relative w-32 h-32 mx-auto mb-4">
                      <Image
                        src={candidate.photo || "/placeholder.svg?height=128&width=128"}
                        alt={candidate.name}
                        fill
                        className="rounded-full object-cover"
                      />
                      {selectedCandidate === candidate.id && (
                        <div className="absolute -top-2 -right-2">
                          <div className="h-8 w-8 bg-primary rounded-full flex items-center justify-center">
                            <CheckCircle className="h-5 w-5 text-primary-foreground" />
                          </div>
                        </div>
                      )}
                    </div>
                    <CardTitle className="text-lg">{candidate.name}</CardTitle>
                    <CardDescription>
                      <div className="space-y-1">
                        <p>NIM: {candidate.nim}</p>
                        <p>{candidate.prodi}</p>
                      </div>
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <div>
                        <p className="text-sm font-semibold text-muted-foreground mb-1">Visi:</p>
                        <p className="text-sm line-clamp-3">{candidate.visi}</p>
                      </div>
                      <Button
                        variant="outline"
                        size="sm"
                        className="w-full bg-transparent"
                        onClick={(e) => {
                          e.stopPropagation()
                          handleShowDetail(candidate)
                        }}
                      >
                        <Eye className="mr-2 h-4 w-4" />
                        Lihat Detail
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>

            {/* Vote Button */}
            <div className="text-center">
              <Button size="lg" onClick={handleConfirmVote} disabled={!selectedCandidate || voting} className="min-w-48">
                {voting ? (
                  <>
                    <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                    Menyimpan Vote...
                  </>
                ) : (
                  <>
                    <Vote className="mr-2 h-5 w-5" />
                    Vote Sekarang
                  </>
                )}
              </Button>
              {selectedCandidate && (
                <p className="text-sm text-muted-foreground mt-2">
                  Anda akan memilih: <strong>{candidates.find((c) => c.id === selectedCandidate)?.name}</strong>
                </p>
              )}
            </div>
          </>
        ) : (
          <Card>
            <CardContent className="pt-6 text-center">
              <AlertCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">Tidak ada kandidat yang tersedia saat ini.</p>
            </CardContent>
          </Card>
        )}

        {/* Confirmation Dialog */}
        <Dialog open={showConfirmDialog} onOpenChange={setShowConfirmDialog}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Konfirmasi Pilihan</DialogTitle>
              <DialogDescription>
                Apakah Anda yakin ingin memilih kandidat berikut? Pilihan tidak dapat diubah setelah dikonfirmasi.
              </DialogDescription>
            </DialogHeader>
            <div className="py-4">
              {selectedCandidate && (
                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center gap-4">
                      <div className="relative w-16 h-16">
                        <Image
                          src={
                            candidates.find((c) => c.id === selectedCandidate)?.photo ||
                            "/placeholder.svg?height=64&width=64"
                          }
                          alt={candidates.find((c) => c.id === selectedCandidate)?.name || ""}
                          fill
                          className="rounded-full object-cover"
                        />
                      </div>
                      <div>
                        <h3 className="font-semibold text-lg">
                          {candidates.find((c) => c.id === selectedCandidate)?.name}
                        </h3>
                        <p className="text-sm text-muted-foreground">
                          {candidates.find((c) => c.id === selectedCandidate)?.nim} â€¢{" "}
                          {candidates.find((c) => c.id === selectedCandidate)?.prodi}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setShowConfirmDialog(false)} disabled={voting}>
                Batal
              </Button>
              <Button onClick={handleVoteSubmit} disabled={voting}>
                {voting ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Menyimpan...
                  </>
                ) : (
                  "Ya, Vote Sekarang"
                )}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        {/* Detail Dialog */}
        <Dialog open={showDetailDialog} onOpenChange={setShowDetailDialog}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Detail Kandidat</DialogTitle>
            </DialogHeader>
            {detailCandidate && (
              <div className="space-y-6">
                <div className="flex items-center gap-4">
                  <div className="relative w-24 h-24">
                    <Image
                      src={detailCandidate.photo || "/placeholder.svg?height=96&width=96"}
                      alt={detailCandidate.name}
                      fill
                      className="rounded-full object-cover"
                    />
                  </div>
                  <div>
                    <h3 className="text-2xl font-bold">{detailCandidate.name}</h3>
                    <p className="text-muted-foreground">
                      NIM: {detailCandidate.nim} â€¢ {detailCandidate.prodi}
                    </p>
                  </div>
                </div>

                <div className="space-y-4">
                  <div>
                    <h4 className="font-semibold text-lg mb-2">Visi</h4>
                    <p className="text-sm leading-relaxed">{detailCandidate.visi}</p>
                  </div>

                  <div>
                    <h4 className="font-semibold text-lg mb-2">Misi</h4>
                    <p className="text-sm leading-relaxed whitespace-pre-line">{detailCandidate.misi}</p>
                  </div>
                </div>
              </div>
            )}
            <DialogFooter>
              <Button variant="outline" onClick={() => setShowDetailDialog(false)}>
                Tutup
              </Button>
              {detailCandidate && (
                <Button
                  onClick={() => {
                    handleCandidateSelect(detailCandidate.id)
                    setShowDetailDialog(false)
                  }}
                  disabled={voting}
                >
                  Pilih Kandidat Ini
                </Button>
              )}
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  )
}

app/waiting/page.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Badge } from "@/components/ui/badge"
import { Clock, CheckCircle, Loader2, RefreshCw } from "lucide-react"
import { useRouter } from "next/navigation"
import { ApiClient } from "@/lib/api-client"
import { Button } from "@/components/ui/button"

export default function WaitingPage() {
  const [user, setUser] = useState<any>(null)
  const [votingSession, setVotingSession] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [timeRemaining, setTimeRemaining] = useState("")
  const router = useRouter()

  useEffect(() => {
    const checkAuthAndData = async () => {
      try {
        const { user: authUser } = await ApiClient.getUser()
        if (!authUser) {
          router.push("/login")
          return
        }

        if (authUser.hasVoted) {
          router.push("/success")
          return
        }

        setUser(authUser)

        // Get current voting session - we'll need to create this API endpoint
        try {
          const sessionData = await ApiClient.getVotingSession(authUser.id)

          if (!sessionData) {
            router.push("/generate-code")
            return
          }

          if (sessionData.isValidated) {
            router.push("/vote")
            return
          }

          setVotingSession(sessionData)
        } catch (err) {
          // No session found, redirect to generate code
          router.push("/generate-code")
          return
        }
      } catch (err) {
        console.error("Error loading data:", err)
        router.push("/login")
      } finally {
        setLoading(false)
      }
    }

    checkAuthAndData()

    // Set up polling for session validation (replacing real-time subscription)
    const interval = setInterval(async () => {
      if (user?.id) {
        try {
          const sessionData = await ApiClient.getVotingSession(user.id)
          if (sessionData?.isValidated) {
            router.push("/vote")
          }
        } catch (err) {
          console.error("Error checking session:", err)
        }
      }
    }, 3000) // Check every 3 seconds

    return () => {
      clearInterval(interval)
    }
  }, [router, user?.id])

  useEffect(() => {
    if (!votingSession) return

    const updateTimer = () => {
      const now = new Date()
      const expiresAt = new Date(votingSession.expiresAt)
      const diff = expiresAt.getTime() - now.getTime()

      if (diff <= 0) {
        setTimeRemaining("Expired")
        return
      }

      const minutes = Math.floor(diff / (1000 * 60))
      const seconds = Math.floor((diff % (1000 * 60)) / 1000)
      setTimeRemaining(`${minutes}:${seconds.toString().padStart(2, "0")}`)
    }

    updateTimer()
    const interval = setInterval(updateTimer, 1000)

    return () => clearInterval(interval)
  }, [votingSession])

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-2xl py-8">
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Clock className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Menunggu Validasi</p>
            </div>
          </div>
        </div>

        <div className="space-y-6">
          {/* Status Card */}
          <Card>
            <CardHeader className="text-center">
              <CardTitle className="flex items-center justify-center gap-2">
                <Clock className="h-5 w-5 text-orange-500" />
                Menunggu Validasi Panitia
              </CardTitle>
              <CardDescription>Kode Anda sedang menunggu untuk divalidasi oleh panitia</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="text-center space-y-4">
                <div className="flex items-center justify-center">
                  <Loader2 className="h-8 w-8 animate-spin text-primary" />
                </div>

                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">Status Kode</p>
                  <Badge variant="secondary" className="text-orange-600 bg-orange-50">
                    Menunggu Validasi
                  </Badge>
                </div>

                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">Waktu Tersisa</p>
                  <div className="text-2xl font-mono font-bold">{timeRemaining}</div>
                </div>

                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">Kode Redeem</p>
                  <div className="inline-flex items-center justify-center p-2 bg-muted rounded">
                    <span className="text-lg font-mono font-bold">{votingSession?.redeemCode}</span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Instructions */}
          <Card className="bg-muted/50">
            <CardHeader>
              <CardTitle className="text-lg">Apa yang harus dilakukan?</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <Alert>
                  <CheckCircle className="h-4 w-4" />
                  <AlertDescription>
                    Kode QR atau redeem code Anda sudah diterima oleh sistem. Silakan tunggu panitia untuk memvalidasi
                    identitas Anda.
                  </AlertDescription>
                </Alert>

                <div className="space-y-3 text-sm">
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-green-500 text-white flex items-center justify-center text-xs font-bold mt-0.5">
                      âœ“
                    </div>
                    <p>Kode berhasil dibuat dan siap untuk validasi</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-orange-500 text-white flex items-center justify-center text-xs font-bold mt-0.5">
                      â³
                    </div>
                    <p>Menunggu panitia memvalidasi identitas Anda</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-gray-300 text-gray-600 flex items-center justify-center text-xs font-bold mt-0.5">
                      3
                    </div>
                    <p>Setelah divalidasi, Anda akan otomatis diarahkan ke halaman voting</p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Help Card */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Butuh Bantuan?</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3 text-sm">
                <p>Jika Anda menunggu terlalu lama atau mengalami masalah:</p>
                <ul className="list-disc list-inside space-y-1 text-muted-foreground">
                  <li>Pastikan Anda berada di lokasi yang telah ditentukan</li>
                  <li>Tunjukkan kode redeem kepada panitia jika QR code bermasalah</li>
                  <li>Hubungi panitia jika kode sudah expired</li>
                </ul>
                <div className="pt-2 border-t">
                  <p>
                    <strong>Kontak Panitia:</strong>
                  </p>
                  <p>Email: pemilu@itera.ac.id</p>
                  <p>WhatsApp: +62 812-3456-7890</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Back to Generate Code */}
          <div className="text-center">
            <Button variant="outline" onClick={() => router.push("/generate-code")}>
              <RefreshCw className="mr-2 h-4 w-4" />
              Kembali ke Generate Code
            </Button>
          </div>
        </div>
      </div>
    </div>
  )
}

