import { NextRequest, NextResponse } from 'next/server'
import { prisma } from '@/lib/prisma'
import bcrypt from 'bcryptjs'

export async function POST(request: NextRequest) {
  try {
    const { email, name, nim, prodi, gender, phone, password } = await request.json()

    if (!email || !name || !nim || !prodi || !gender || !password) {
      return NextResponse.json(
        { error: 'All required fields must be filled' },
        { status: 400 }
      )
    }

    if (password.length < 6) {
      return NextResponse.json(
        { error: 'Password must be at least 6 characters long' },
        { status: 400 }
      )
    }

    // Validate ITERA email format
    if (!email.endsWith('@student.itera.ac.id') && !email.endsWith('@itera.ac.id')) {
      return NextResponse.json(
        { error: 'Must use ITERA email address (@itera.ac.id or @student.itera.ac.id)' },
        { status: 400 }
      )
    }

    // Check if user already exists
    const existingUser = await prisma.user.findFirst({
      where: {
        OR: [
          { email },
          { nim }
        ]
      }
    })

    if (existingUser) {
      return NextResponse.json(
        { error: 'User with this email or NIM already exists' },
        { status: 400 }
      )
    }

    // Hash password with bcrypt
    const hashedPassword = await bcrypt.hash(password, 10)

    // Create new user with hashed password
    const user = await prisma.user.create({
      data: {
        email,
        name,
        nim,
        prodi,
        gender,
        phone: phone || null,
        password: hashedPassword
      }
    })

    return NextResponse.json({
      user: {
        id: user.id,
        email: user.email,
        name: user.name,
