"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Badge } from "@/components/ui/badge"
import { QrCode, RefreshCw, Clock, User, Loader2, CheckCircle, AlertCircle } from "@/lib/icons"
import { useRouter } from "next/navigation"
import QRCodeDisplay from "@/components/qr-code-display"
import { getMockUser, mockDatabase } from "@/lib/mock-auth"

export default function GenerateCodePage() {
  const [user, setUser] = useState<any>(null)
  const [votingSession, setVotingSession] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [generating, setGenerating] = useState(false)
  const [error, setError] = useState("")
  const [timeRemaining, setTimeRemaining] = useState("")
  const router = useRouter()

  useEffect(() => {
    const checkAuthAndData = async () => {
      try {
        const mockUser = getMockUser()
        if (!mockUser) {
          router.push("/login")
          return
        }

        if (mockUser.hasVoted) {
          router.push("/success")
          return
        }

        setUser(mockUser)

        // Check for existing voting session
        if (mockDatabase.votingSession) {
          const now = new Date()
          const expiresAt = new Date(mockDatabase.votingSession.expiresAt)

          if (now < expiresAt && !mockDatabase.votingSession.isUsed) {
            setVotingSession(mockDatabase.votingSession)
            if (mockDatabase.votingSession.isValidated) {
              router.push("/vote")
              return
            }
          } else {
            // Clear expired session
            mockDatabase.votingSession = null
          }
        }
      } catch (err) {
        setError("Terjadi kesalahan saat memuat data")
      } finally {
        setLoading(false)
      }
    }

    checkAuthAndData()
  }, [router])

  // Update time remaining every second
  useEffect(() => {
    if (!votingSession) return

    const updateTime = () => {
      const now = new Date()
      const expiresAt = new Date(votingSession.expiresAt)
      const diff = expiresAt.getTime() - now.getTime()

      if (diff <= 0) {
        setTimeRemaining("Expired")
        // Clear expired session
        mockDatabase.votingSession = null
        setVotingSession(null)
        return
      }

      const minutes = Math.floor(diff / (1000 * 60))
      const seconds = Math.floor((diff % (1000 * 60)) / 1000)
      setTimeRemaining(`${minutes}:${seconds.toString().padStart(2, "0")}`)
    }

    updateTime()
    const interval = setInterval(updateTime, 1000)

    return () => clearInterval(interval)
  }, [votingSession])

  // Check for validation status periodically
  useEffect(() => {
    if (!votingSession || votingSession.isValidated) return

    const checkValidation = () => {
      if (mockDatabase.votingSession?.isValidated) {
        setVotingSession({ ...mockDatabase.votingSession })
        router.push("/vote")
      }
    }

    const interval = setInterval(checkValidation, 2000)
    return () => clearInterval(interval)
  }, [votingSession, router])

  const generateQRCode = async () => {
    if (!user) return

    setGenerating(true)
    setError("")

    try {
      // Simulate API delay
      await new Promise((resolve) => setTimeout(resolve, 1000))

      const session = mockDatabase.generateQRCode()
      if (session) {
        setVotingSession(session)
      } else {
        setError("Gagal membuat kode QR")
      }
    } catch (err) {
      setError("Terjadi kesalahan saat membuat kode QR")
    } finally {
      setGenerating(false)
    }
  }

  const refreshCode = async () => {
    if (!votingSession) return

    // Clear current session and generate new one
    mockDatabase.votingSession = null
    setVotingSession(null)
    await generateQRCode()
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-2xl py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <QrCode className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Generate Kode Voting</p>
            </div>
          </div>
        </div>

        {error && (
          <Alert variant="destructive" className="mb-6">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {/* User Info */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <User className="h-5 w-5" />
              Informasi Pemilih
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <p className="text-sm text-muted-foreground">Nama</p>
                <p className="font-semibold">{user?.name}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Email</p>
                <p className="font-semibold">{user?.email}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Role</p>
                <p className="font-semibold">{user?.role}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Status</p>
                <Badge variant="secondary">Belum Voting</Badge>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* QR Code Section */}
        {!votingSession ? (
          <Card>
            <CardHeader className="text-center">
              <CardTitle>Generate Kode Voting</CardTitle>
              <CardDescription>
                Klik tombol di bawah untuk membuat kode QR dan kode redeem yang akan digunakan untuk validasi voting
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center">
              <Button onClick={generateQRCode} disabled={generating} size="lg">
                {generating ? (
                  <>
                    <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                    Membuat Kode...
                  </>
                ) : (
                  <>
                    <QrCode className="mr-2 h-5 w-5" />
                    Generate Kode QR
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {/* QR Code Display */}
            <Card>
              <CardHeader className="text-center">
                <CardTitle className="flex items-center justify-center gap-2">
                  <QrCode className="h-5 w-5" />
                  Kode QR Voting
                </CardTitle>
                <CardDescription>Tunjukkan kode QR ini kepada panitia untuk validasi</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center space-y-4">
                  <QRCodeDisplay value={votingSession.qrCode} size={200} />

                  <div className="flex items-center justify-center gap-2">
                    <Clock className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm text-muted-foreground">Berlaku hingga: {timeRemaining}</span>
                  </div>

                  {votingSession.isValidated ? (
                    <Alert>
                      <CheckCircle className="h-4 w-4" />
                      <AlertDescription>
                        Kode Anda sudah divalidasi! Anda akan diarahkan ke halaman voting.
                      </AlertDescription>
                    </Alert>
                  ) : (
                    <Button onClick={refreshCode} variant="outline" size="sm">
                      <RefreshCw className="mr-2 h-4 w-4" />
                      Generate Ulang
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Redeem Code */}
            <Card>
              <CardHeader className="text-center">
                <CardTitle>Kode Redeem</CardTitle>
                <CardDescription>Alternatif jika QR code tidak dapat dipindai</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center">
                  <div className="inline-flex items-center justify-center p-4 bg-muted rounded-lg">
                    <span className="text-2xl font-mono font-bold tracking-wider">{votingSession.redeemCode}</span>
                  </div>
                  <p className="text-sm text-muted-foreground mt-2">
                    Berikan kode ini kepada panitia jika QR code tidak dapat dipindai
                  </p>
                </div>
              </CardContent>
            </Card>

            {/* Instructions */}
            <Card className="bg-muted/50">
              <CardHeader>
                <CardTitle className="text-lg">Langkah Selanjutnya</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 text-sm">
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      1
                    </div>
                    <p>Tunjukkan kode QR atau berikan kode redeem kepada panitia</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      2
                    </div>
                    <p>Tunggu panitia memvalidasi identitas dan kode Anda</p>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="h-6 w-6 rounded-full bg-primary text-primary-foreground flex items-center justify-center text-xs font-bold mt-0.5">
                      3
                    </div>
                    <p>Setelah divalidasi, Anda akan diarahkan ke halaman voting</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Mock Validation Button for Testing */}
            <Card className="bg-yellow-50 border-yellow-200">
              <CardHeader>
                <CardTitle className="text-lg text-yellow-800">Testing Mode</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-sm text-yellow-700 mb-4">
                  Untuk testing, klik tombol di bawah untuk mensimulasikan validasi oleh admin:
                </p>
                <Button
                  onClick={() => {
                    mockDatabase.validateQRCode(votingSession.redeemCode)
                    setVotingSession({ ...mockDatabase.votingSession })
                  }}
                  variant="outline"
                  className="w-full"
                  disabled={votingSession.isValidated}
                >
                  {votingSession.isValidated ? "Sudah Divalidasi" : "Simulasi Validasi Admin"}
                </Button>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  )
}

app/register/page.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Vote, User, Loader2, CheckCircle } from "lucide-react"
import { useRouter } from "next/navigation"
import { createClient } from "@/lib/supabase/client"

const PRODI_OPTIONS = [
  "Teknik Informatika",
  "Teknik Sipil",
  "Teknik Elektro",
  "Teknik Kimia",
  "Teknik Mesin",
  "Teknik Industri",
  "Teknik Geologi",
  "Teknik Geofisika",
  "Teknik Lingkungan",
  "Arsitektur",
  "Perencanaan Wilayah dan Kota",
  "Teknik Biosistem",
  "Sains Aktuaria",
  "Matematika",
  "Fisika",
  "Kimia",
  "Biologi",
  "Farmasi",
]

export default function RegisterPage() {
  const [formData, setFormData] = useState({
    nim: "",
    name: "",
    prodi: "",
    gender: "",
    phone: "",
  })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [userEmail, setUserEmail] = useState("")
  const router = useRouter()
  const supabase = createClient()

  useEffect(() => {
    const checkAuth = async () => {
      const {
        data: { user },
      } = await supabase.auth.getUser()
      if (!user) {
        router.push("/login")
        return
      }
      setUserEmail(user.email || "")

      // Check if user already registered
      const { data: userData } = await supabase.from("User").select("*").eq("email", user.email).single()

      if (userData) {
        // User already registered, redirect based on status
        if (userData.hasVoted) {
          router.push("/success")
        } else {
          router.push("/generate-code")
        }
      }
    }

    checkAuth()
  }, [router, supabase])

  const handleInputChange = (field: string, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }))
  }

  const validateNIM = (nim: string) => {
    // Basic NIM validation for ITERA (usually 9 digits starting with specific patterns)
    const nimPattern = /^[0-9]{9}$/
    return nimPattern.test(nim)
  }

  const validatePhone = (phone: string) => {
    // Indonesian phone number validation
    const phonePattern = /^(\+62|62|0)[0-9]{9,12}$/
    return phonePattern.test(phone)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    // Validation
    if (!validateNIM(formData.nim)) {
      setError("NIM harus berupa 9 digit angka")
      setLoading(false)
      return
    }

    if (formData.phone && !validatePhone(formData.phone)) {
      setError("Format nomor telepon tidak valid")
      setLoading(false)
      return
    }

    try {
      // Check if NIM already exists
      const { data: existingUser } = await supabase.from("User").select("nim").eq("nim", formData.nim).single()

      if (existingUser) {
        setError("NIM sudah terdaftar dalam sistem")
        setLoading(false)
        return
      }

      // Insert new user
      const { error: insertError } = await supabase.from("User").insert({
        email: userEmail,
        nim: formData.nim,
        name: formData.name,
        prodi: formData.prodi,
        gender: formData.gender,
        phone: formData.phone || null,
        role: "VOTER",
      })

      if (insertError) {
        setError("Gagal menyimpan data: " + insertError.message)
        return
      }

      // Registration successful, redirect to generate code
      router.push("/generate-code")
    } catch (err) {
      setError("Terjadi kesalahan saat registrasi")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Vote className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Registrasi Pemilih</p>
            </div>
          </div>
        </div>

        {/* Registration Form */}
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl flex items-center justify-center gap-2">
              <User className="h-6 w-6" />
              Lengkapi Data Diri
            </CardTitle>
            <CardDescription>
              Silakan lengkapi data diri Anda untuk dapat berpartisipasi dalam pemilihan
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              {/* Email (Read-only) */}
              <div className="space-y-2">
                <Label htmlFor="email">Email Mahasiswa</Label>
                <Input id="email" type="email" value={userEmail} disabled className="bg-muted" />
                <p className="text-xs text-muted-foreground">Email ini sudah terverifikasi dari sistem login</p>
              </div>

              {/* NIM */}
              <div className="space-y-2">
                <Label htmlFor="nim">NIM (Nomor Induk Mahasiswa) *</Label>
                <Input
                  id="nim"
                  type="text"
                  placeholder="Contoh: 121450001"
                  value={formData.nim}
                  onChange={(e) => handleInputChange("nim", e.target.value)}
                  required
                  disabled={loading}
                  maxLength={9}
                />
                <p className="text-xs text-muted-foreground">Masukkan 9 digit NIM Anda</p>
              </div>

              {/* Name */}
              <div className="space-y-2">
                <Label htmlFor="name">Nama Lengkap *</Label>
                <Input
                  id="name"
                  type="text"
                  placeholder="Masukkan nama lengkap sesuai KTM"
                  value={formData.name}
                  onChange={(e) => handleInputChange("name", e.target.value)}
                  required
                  disabled={loading}
                />
              </div>

              {/* Program Studi */}
              <div className="space-y-2">
                <Label htmlFor="prodi">Program Studi *</Label>
                <Select
                  value={formData.prodi}
                  onValueChange={(value) => handleInputChange("prodi", value)}
                  disabled={loading}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Pilih program studi Anda" />
                  </SelectTrigger>
                  <SelectContent>
                    {PRODI_OPTIONS.map((prodi) => (
                      <SelectItem key={prodi} value={prodi}>
                        {prodi}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Gender */}
              <div className="space-y-2">
                <Label htmlFor="gender">Jenis Kelamin *</Label>
                <Select
                  value={formData.gender}
                  onValueChange={(value) => handleInputChange("gender", value)}
                  disabled={loading}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Pilih jenis kelamin" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="L">Laki-laki</SelectItem>
                    <SelectItem value="P">Perempuan</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Phone */}
              <div className="space-y-2">
                <Label htmlFor="phone">Nomor Telepon</Label>
                <Input
                  id="phone"
                  type="tel"
                  placeholder="Contoh: 081234567890"
                  value={formData.phone}
                  onChange={(e) => handleInputChange("phone", e.target.value)}
                  disabled={loading}
                />
                <p className="text-xs text-muted-foreground">Opsional - untuk keperluan komunikasi jika diperlukan</p>
              </div>

              <Button type="submit" className="w-full" disabled={loading}>
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Menyimpan Data...
                  </>
                ) : (
                  <>
                    <CheckCircle className="mr-2 h-4 w-4" />
                    Daftar Sebagai Pemilih
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        {/* Info Card */}
        <Card className="mt-6 bg-muted/50">
          <CardContent className="pt-6">
            <div className="text-center">
              <h3 className="font-semibold mb-2">Informasi Penting</h3>
              <div className="text-sm text-muted-foreground space-y-2">
                <p> Pastikan data yang Anda masukkan sesuai dengan KTM</p>
                <p> NIM yang sudah terdaftar tidak dapat digunakan lagi</p>
                <p> Setelah registrasi, Anda akan mendapatkan kode QR untuk voting</p>
                <p> Hubungi panitia jika mengalami kesulitan: pemilu@itera.ac.id</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

app/vote/page.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Vote, User, CheckCircle, Loader2, AlertCircle, Eye } from "@/lib/icons"
import { useRouter } from "next/navigation"
import Image from "next/image"
import { getMockUser, mockDatabase } from "@/lib/mock-auth"

interface Candidate {
  id: string
  name: string
  nim: string
  prodi: string
  visi: string
  misi: string
  photo?: string
  isActive: boolean
}

export default function VotePage() {
  const [user, setUser] = useState<any>(null)
  const [candidates, setCandidates] = useState<Candidate[]>([])
  const [selectedCandidate, setSelectedCandidate] = useState<string>("")
  const [loading, setLoading] = useState(true)
  const [voting, setVoting] = useState(false)
  const [error, setError] = useState("")
  const [showConfirmDialog, setShowConfirmDialog] = useState(false)
  const [showDetailDialog, setShowDetailDialog] = useState(false)
  const [detailCandidate, setDetailCandidate] = useState<Candidate | null>(null)
  const router = useRouter()

  useEffect(() => {
    const checkAuthAndLoadData = async () => {
      try {
        const mockUser = getMockUser()
        if (!mockUser) {
          router.push("/login")
          return
        }

        if (mockUser.hasVoted) {
          router.push("/success")
          return
        }

        // Check if user has validated voting session
        if (
          !mockDatabase.votingSession ||
          !mockDatabase.votingSession.isValidated ||
          mockDatabase.votingSession.isUsed
        ) {
          router.push("/generate-code")
          return
        }

        setUser(mockUser)

        // Load candidates from mock database
        setCandidates(mockDatabase.candidates.filter((c) => c.isActive))
      } catch (err) {
        setError("Terjadi kesalahan saat memuat data")
      } finally {
        setLoading(false)
      }
    }

    checkAuthAndLoadData()
  }, [router])

  const handleVoteSubmit = async () => {
    if (!selectedCandidate || !user) return

    setVoting(true)
    setError("")

    try {
      // Simulate API delay
      await new Promise((resolve) => setTimeout(resolve, 1500))

      // Submit vote using mock database
      const success = mockDatabase.submitVote(selectedCandidate)

      if (!success) {
        setError("Gagal menyimpan vote")
        setVoting(false)
        return
      }

      // Success - redirect to success page
      router.push("/success")
    } catch (err) {
      setError("Terjadi kesalahan saat menyimpan vote")
      setVoting(false)
    }
  }

  const handleCandidateSelect = (candidateId: string) => {
    setSelectedCandidate(candidateId)
  }

  const handleShowDetail = (candidate: Candidate) => {
    setDetailCandidate(candidate)
    setShowDetailDialog(true)
  }

  const handleConfirmVote = () => {
    if (!selectedCandidate) return
    setShowConfirmDialog(true)
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Memuat data kandidat...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background p-4">
      <div className="container mx-auto max-w-4xl py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="h-12 w-12 rounded-lg bg-primary flex items-center justify-center">
              <Vote className="h-7 w-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="font-bold text-xl text-foreground">ITERA Election</h1>
              <p className="text-sm text-muted-foreground">Pilih Kandidat</p>
            </div>
          </div>
        </div>

        {error && (
          <Alert variant="destructive" className="mb-6">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {/* Voter Info */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <User className="h-5 w-5" />
              Informasi Pemilih
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <p className="text-sm text-muted-foreground">Nama</p>
                <p className="font-semibold">{user?.name}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Email</p>
                <p className="font-semibold">{user?.email}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Role</p>
                <p className="font-semibold">{user?.role}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Instructions */}
        <Alert className="mb-8">
          <CheckCircle className="h-4 w-4" />
          <AlertDescription>
            <strong>Petunjuk Voting:</strong> Pilih salah satu kandidat di bawah ini dengan mengklik kartu kandidat.
            Setelah memilih, klik tombol "Vote" untuk mengonfirmasi pilihan Anda. Pilihan tidak dapat diubah setelah
            dikonfirmasi.
          </AlertDescription>
        </Alert>

        {/* Candidates Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {candidates.map((candidate) => (
            <Card
              key={candidate.id}
              className={`cursor-pointer transition-all duration-200 hover:shadow-lg ${
                selectedCandidate === candidate.id ? "ring-2 ring-primary bg-primary/5" : "hover:shadow-md"
              }`}
              onClick={() => handleCandidateSelect(candidate.id)}
            >
              <CardHeader className="text-center">
                <div className="relative w-32 h-32 mx-auto mb-4">
                  <Image
                    src={candidate.photo || "/placeholder.svg?height=128&width=128"}
                    alt={candidate.name}
                    fill
                    className="rounded-full object-cover"
                  />
                  {selectedCandidate === candidate.id && (
                    <div className="absolute -top-2 -right-2">
                      <div className="h-8 w-8 bg-primary rounded-full flex items-center justify-center">
                        <CheckCircle className="h-5 w-5 text-primary-foreground" />
                      </div>
                    </div>
                  )}
                </div>
                <CardTitle className="text-lg">{candidate.name}</CardTitle>
                <CardDescription>
                  <div className="space-y-1">
                    <p>NIM: {candidate.nim}</p>
                    <p>{candidate.prodi}</p>
                  </div>
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <div>
                    <p className="text-sm font-semibold text-muted-foreground mb-1">Visi:</p>
                    <p className="text-sm line-clamp-3">{candidate.visi}</p>
                  </div>
                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full bg-transparent"
                    onClick={(e) => {
                      e.stopPropagation()
                      handleShowDetail(candidate)
                    }}
                  >
                    <Eye className="mr-2 h-4 w-4" />
                    Lihat Detail
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Vote Button */}
        <div className="text-center">
          <Button size="lg" onClick={handleConfirmVote} disabled={!selectedCandidate || voting} className="min-w-48">
            {voting ? (
              <>
                <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                Menyimpan Vote...
              </>
            ) : (
              <>
                <Vote className="mr-2 h-5 w-5" />
                Vote Sekarang
              </>
            )}
          </Button>
          {selectedCandidate && (
            <p className="text-sm text-muted-foreground mt-2">
              Anda akan memilih: <strong>{candidates.find((c) => c.id === selectedCandidate)?.name}</strong>
            </p>
          )}
        </div>

        {/* Confirmation Dialog */}
        <Dialog open={showConfirmDialog} onOpenChange={setShowConfirmDialog}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Konfirmasi Pilihan</DialogTitle>
              <DialogDescription>
                Apakah Anda yakin ingin memilih kandidat berikut? Pilihan tidak dapat diubah setelah dikonfirmasi.
              </DialogDescription>
            </DialogHeader>
            <div className="py-4">
              {selectedCandidate && (
                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center gap-4">
                      <div className="relative w-16 h-16">
                        <Image
                          src={
                            candidates.find((c) => c.id === selectedCandidate)?.photo ||
                            "/placeholder.svg?height=64&width=64" ||
                            "/placeholder.svg" ||
                            "/placeholder.svg"
                          }
                          alt={candidates.find((c) => c.id === selectedCandidate)?.name || ""}
                          fill
                          className="rounded-full object-cover"
                        />
                      </div>
                      <div>
                        <h3 className="font-semibold text-lg">
                          {candidates.find((c) => c.id === selectedCandidate)?.name}
                        </h3>
                        <p className="text-sm text-muted-foreground">
                          {candidates.find((c) => c.id === selectedCandidate)?.nim} {" "}
                          {candidates.find((c) => c.id === selectedCandidate)?.prodi}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setShowConfirmDialog(false)}>
                Batal
              </Button>
              <Button onClick={handleVoteSubmit} disabled={voting}>
                {voting ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Menyimpan...
                  </>
                ) : (
                  "Ya, Vote Sekarang"
                )}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        {/* Detail Dialog */}
        <Dialog open={showDetailDialog} onOpenChange={setShowDetailDialog}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Detail Kandidat</DialogTitle>
            </DialogHeader>
            {detailCandidate && (
              <div className="space-y-6">
                <div className="flex items-center gap-4">
                  <div className="relative w-24 h-24">
                    <Image
                      src={detailCandidate.photo || "/placeholder.svg?height=96&width=96"}
                      alt={detailCandidate.name}
                      fill
                      className="rounded-full object-cover"
                    />
                  </div>
                  <div>
                    <h3 className="text-2xl font-bold">{detailCandidate.name}</h3>
                    <p className="text-muted-foreground">
                      NIM: {detailCandidate.nim}  {detailCandidate.prodi}
                    </p>
                  </div>
                </div>

                <div className="space-y-4">
                  <div>
                    <h4 className="font-semibold text-lg mb-2">Visi</h4>
                    <p className="text-sm leading-relaxed">{detailCandidate.visi}</p>
                  </div>

                  <div>
                    <h4 className="font-semibold text-lg mb-2">Misi</h4>
                    <p className="text-sm leading-relaxed whitespace-pre-line">{detailCandidate.misi}</p>
                  </div>
                </div>
              </div>
            )}
            <DialogFooter>
              <Button variant="outline" onClick={() => setShowDetailDialog(false)}>
                Tutup
              </Button>
              {detailCandidate && (
                <Button
                  onClick={() => {
                    handleCandidateSelect(detailCandidate.id)
                    setShowDetailDialog(false)
                  }}
                >
                  Pilih Kandidat Ini
                </Button>
              )}
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  )
}

